var mapboxUrl = 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
	mapboxAttribution = '&copy <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
	mapCenter = [getUrlParameter('lat'), getUrlParameter('lng')],
	mapstyle = L.tileLayer(mapboxUrl, {id: 'mapbox.light', attribution: mapboxAttribution, maxNativeZoom: 19, maxZoom: 19, updateWhenZooming: false}),
	map = new L.map('map',{layer: mapstyle, center: [mapCenter[0], mapCenter[1]], zoom: 19, minZoom:19, maxZoom:19, updateWhenZooming: false}),
	marker = new L.Marker([getUrlParameter('lat'), getUrlParameter('lng')]);
mapstyle.addTo(map);
marker.addTo(map);
map.dragging.disable();
